      console.log('新的页号为' + pageIndex);
  });
  comp.on('sizeChange', function(arg) {
      console.log('每页显示条数为' + arg[0]);
  });




无边框的分页在含有u-pagination的div元素中添加u-pagination-no-border样式即可

